import React, { useEffect, useState } from "react";
import ReactDOM from "react-dom";
import { ApolloClient, InMemoryCache, gql, useQuery } from "@apollo/client";
// import Flags from "country-flag-icons/react/3x2";
import Header from "./components/header";
import Footer from "./components/footer";
import { Row, Col, Button, Card } from "reactstrap";
import SearchIcon from "@material-ui/icons/Search";
import { Grid, Paper } from "@material-ui/core";
// initialize a GraphQL client

const client = new ApolloClient({
  cache: new InMemoryCache(),
  uri: "https://countries.trevorblades.com",
});


const CountryList = () => {
  const [cont, setCont] = useState("1");

  const COUNTRIES_QUERY = gql`
    query Counties {
      countries {
        name
        code
        capital
        currency
        continent {
          name
          code
        }
        languages {
          code
          name
        }
      }
    }
  `;

  const { data, loading } = useQuery(COUNTRIES_QUERY, {
    client,
  });

  function capital(string) {
    var capitalize = "";
    capitalize = console.log(
      string.charAt(0).toUpperCase() + string.slice(1).toLowerCase()
    );
    return capitalize;
  }

  const [approvalVariable, setApprovalVariable] = useState("");

  if (loading) {
    return <p>Loading...</p>;
  }

  const { countries } = data;

  let filteredData = countries.filter((x) =>
    String(x.name).includes(approvalVariable)
  );

  return (
    <>
      <Header></Header>

      <Row style={{ marginTop: 20, marginBottom: 20 }}>
        <Col>
          <h3>Some random text {"\n"}</h3>
          <div>
            <SearchIcon style={{ fontSize: 20 }} />
            <input
              type="text"
              style={{ borderRadius: 5 }}
              value={approvalVariable}
              onChange={(event) => {
                setApprovalVariable(event.target.value);
                console.log(event.target.value);
              }}
            />
            {"  "}
            <button
              class="btn btn-outline-secondary"
              type="button"
              id="button-addon2"
              style={{ borderRadius: 5 }}
              onClick={() => {
                let gg = capital(approvalVariable);
                setApprovalVariable(gg);
              }}
            >
              Button
            </button>
          </div>
        </Col>
      </Row>

      <Card>
        <Row style={{ flexDirection: "row", alignSelf: "auto" }}>
          <h2>Group by</h2>

          <Button variant="text" onClick={() => setCont("1")}>
            Continent
          </Button>
          <Button type="buttton" color="primary" onClick={() => setCont("0")}>
            Language
          </Button>
        </Row>
      </Card>

      {/* ========== Search by continent ========== */}
      {cont === "1" ? (
        <div>
          {countries.length === 0
            ? null
            : filteredData
                .filter((conti) => conti.continent.code === "AN")
                .slice(0, 1)
                .map((c, i) => (
                  <div key={c.code}>
                    <h1>
                      {" "}
                      <li>Antarctica</li>
                    </h1>
                  </div>
                ))}

          {countries.length === 0
            ? null
            : filteredData
                .filter((conti) => conti.continent.code.includes("AN"))
                .map((c, i) => (
                  <Grid item xs={10} sm={5} style={{ padding: 10 }}>
                    <Paper>
                      <div
                        style={{
                          backgroundColor: "gray",
                          borderRadius: 7,
                          color: "white",
                        }}
                      >
                        <h3 style={{ padding: 10 }}>{c.name}</h3>
                        <ul style={{ padding: 5, marginLeft: 20 }}>
                          <li>
                            <strong>Capital: </strong>
                            <span>{c.capital}</span>
                          </li>
                          <li>
                            <strong>Currency: </strong>
                            <span>{c.currency}</span>
                          </li>
                        </ul>
                      </div>
                    </Paper>
                  </Grid>
                ))}

          {/* ==================== */}

          {countries.length === 0
            ? null
            : filteredData
                .filter((conti) => conti.continent.code === "AS")
                .slice(0, 1)
                .map((c, i) => (
                  <div key={c.code}>
                    <h1>
                      <li>Asia</li>
                    </h1>
                  </div>
                ))}

          {countries.length === 0
            ? null
            : filteredData
                .filter((conti) => conti.continent.code.includes("AS"))
                .map((c, i) => (
                  <Grid item xs={10} sm={5} style={{ padding: 10 }}>
                    <Paper>
                      <div
                        style={{
                          backgroundColor: "gray",
                          borderRadius: 7,
                          color: "white",
                        }}
                      >
                        <h3 style={{ padding: 10 }}>{c.name}</h3>
                        <ul style={{ padding: 5, marginLeft: 20 }}>
                          <li>
                            <strong>Capital: </strong>
                            <span>{c.capital}</span>
                          </li>
                          <li>
                            <strong>Currency: </strong>
                            <span>{c.currency}</span>
                          </li>
                        </ul>
                      </div>
                    </Paper>
                  </Grid>
                ))}

          {/* ==================== */}

          {countries.length === 0
            ? null
            : filteredData
                .filter((conti) => conti.continent.code === "EU")
                .slice(0, 1)
                .map((c, i) => (
                  <div key={c.code}>
                    <h1>
                      <li>Europe</li>
                    </h1>
                  </div>
                ))}

          {countries.length === 0
            ? null
            : filteredData
                .filter((conti) => conti.continent.code.includes("EU"))
                .map((c, i) => (
                  <Grid item xs={10} sm={5} style={{ padding: 10 }}>
                    <Paper>
                      <div
                        style={{
                          backgroundColor: "gray",
                          borderRadius: 7,
                          color: "white",
                        }}
                      >
                        <h3 style={{ padding: 10 }}>{c.name}</h3>
                        <ul style={{ padding: 5, marginLeft: 20 }}>
                          <li>
                            <strong>Capital: </strong>
                            <span>{c.capital}</span>
                          </li>
                          <li>
                            <strong>Currency: </strong>
                            <span>{c.currency}</span>
                          </li>
                        </ul>
                      </div>
                    </Paper>
                  </Grid>
                ))}

          {/* ==================== */}

          {countries.length === 0
            ? null
            : filteredData
                .filter((conti) => conti.continent.code === "NA")
                .slice(0, 1)
                .map((c, i) => (
                  <div key={c.code}>
                    <h1>
                      <li>North America</li>
                    </h1>
                  </div>
                ))}

          {countries.length === 0
            ? null
            : filteredData
                .filter((conti) => conti.continent.code.includes("NA"))
                .map((c, i) => (
                  <Grid item xs={10} sm={5} style={{ padding: 10 }}>
                    <Paper>
                      <div
                        style={{
                          backgroundColor: "gray",
                          borderRadius: 7,
                          color: "white",
                        }}
                      >
                        <h3 style={{ padding: 10 }}>{c.name}</h3>
                        <ul style={{ padding: 5, marginLeft: 20 }}>
                          <li>
                            <strong>Capital: </strong>
                            <span>{c.capital}</span>
                          </li>
                          <li>
                            <strong>Currency: </strong>
                            <span>{c.currency}</span>
                          </li>
                        </ul>
                      </div>
                    </Paper>
                  </Grid>
                ))}

          {/* ==================== */}

          {countries.length === 0
            ? null
            : filteredData
                .filter((conti) => conti.continent.code === "OC")
                .slice(0, 1)
                .map((c, i) => (
                  <div key={c.code}>
                    <h1>
                      <li>Oceania</li>
                    </h1>
                  </div>
                ))}

          {countries.length === 0
            ? null
            : filteredData
                .filter((conti) => conti.continent.code.includes("OC"))
                .map((c, i) => (
                  <Grid item xs={10} sm={5} style={{ padding: 10 }}>
                    <Paper>
                      <div
                        style={{
                          backgroundColor: "gray",
                          borderRadius: 7,
                          color: "white",
                        }}
                      >
                        <h3 style={{ padding: 10 }}>{c.name}</h3>
                        <ul style={{ padding: 5, marginLeft: 20 }}>
                          <li>
                            <strong>Capital: </strong>
                            <span>{c.capital}</span>
                          </li>
                          <li>
                            <strong>Currency: </strong>
                            <span>{c.currency}</span>
                          </li>
                        </ul>
                      </div>
                    </Paper>
                  </Grid>
                ))}

          {/* ==================== */}

          {countries.length === 0
            ? null
            : filteredData
                .filter((conti) => conti.continent.code === "SA")
                .slice(0, 1)
                .map((c, i) => (
                  <div key={c.code}>
                    <h1>
                      <li>South America</li>
                    </h1>
                  </div>
                ))}

          {countries.length === 0
            ? null
            : filteredData
                .filter((conti) => conti.continent.code.includes("SA"))
                .map((c, i) => (
                  <Grid item xs={10} sm={5} style={{ padding: 10 }}>
                    <Paper>
                      <div
                        style={{
                          backgroundColor: "gray",
                          borderRadius: 7,
                          color: "white",
                        }}
                      >
                        <h3 style={{ padding: 10 }}>{c.name}</h3>
                        <ul style={{ padding: 5, marginLeft: 20 }}>
                          <li>
                            <strong>Capital: </strong>
                            <span>{c.capital}</span>
                          </li>
                          <li>
                            <strong>Currency: </strong>
                            <span>{c.currency}</span>
                          </li>
                        </ul>
                      </div>
                    </Paper>
                  </Grid>
                ))}
        </div>
      ) : (
        <div>
          <h1>By language</h1>
          {/* lgica para busqueda por language */}
        </div>
      )}
      <Footer></Footer>
    </>
  );
};

ReactDOM.render(<CountryList />, document.getElementById("root"));
