import React, { useEffect, useState } from "react";
import ReactDOM from "react-dom";
import { ApolloClient, InMemoryCache, gql, useQuery } from "@apollo/client";
import Flags from "country-flag-icons/react/3x2";
import Header from "./components/header";
import Footer from "./components/footer";
import { Label, Row, Col, Input } from "reactstrap";
import SearchIcon from "@material-ui/icons/Search";
// initialize a GraphQL client
const client = new ApolloClient({
  cache: new InMemoryCache(),
  uri: "https://countries.trevorblades.com",
});

// write a GraphQL query that asks for names and codes for all countries
const LIST_COUNTRIES = gql`
  {
    countries {
      name
      code
      native
      capital
      emoji
      currency
      languages {
        code
        name
      }
    }
  }
`;

// create a component that renders a select input for coutries
const CountrySelect = () => {

  const [paiss, setPais] = useState([]);
  const [tablaPais, setTablaPais] = useState([]);
  const [busqueda, setBusqueda] = useState("");


  const [locale, setLocale] = useState("");
  const [local, setLocal] = useState(locale);

  const obtener = () => {
    setLocale(window.localStorage.getItem("PAIS"));
  };

  useEffect(() => {
    obtener();
    const item = () => {
      setLocal(window.localStorage.getItem("PAIS"));
    };

    // setLocal(window.localStorage.getItem("PAIS"))
    item();
  }, [local, locale]);

  const [country, setCountry] = useState("");

  useEffect(() => {
    obtener();
    const item = () => {
      setCountry(window.localStorage.getItem("PAIS"));
    };

    // setLocal(window.localStorage.getItem("PAIS"))
    item();
  }, [country]);

  const [monea, setMonea] = useState("");
  // const [pais, setPais] = useState('');
  const { data, loading, error } = useQuery(LIST_COUNTRIES, { client });

  const getPais = async () => {
    // const response = await axios.get("/paises");
    setPais(data);
    setTablaPais(data);
  };
  // getPais();

  // const filtrar = (terminoBusqueda) => {
  //   const resultadosBusqueda = tablaPais.filter((elemento) => {
  //     if (elemento.countries.toString().toLowerCase().includes(terminoBusqueda.toLowerCase())
  //     ) {
  //       return elemento;
  //     }
  //   });
  //   setPais(resultadosBusqueda);
  // };

  const filtrar=(terminoBusqueda)=>{
    const resultadosBusqueda=tablaPais.filter((elemento)=>{
      if(elemento.name.toString().toLowerCase().includes(terminoBusqueda.toLowerCase())
      // || elemento.company.name.toString().toLowerCase().includes(terminoBusqueda.toLowerCase())
      ){
        return elemento;
      }
    });
    // setPais(resultadosBusqueda);
    console.log(resultadosBusqueda)
  }

  const handleChange = e => {
    setBusqueda(e.target.value);
    filtrar(e.target.value);
  }

// const buttonPais = () => {
//     getPais();
//   };  


  useEffect(() => {
    getPais();
    }, [paiss]);

  // const flagNationCode = ''
  // const Bandera = (flagNationCode) => {
  //   return <Flags.`${}` />;
  // };

  const defMonea = (item) => {
    return `"${item}"`;
  };
  const pais = (countrys) => {
    return (
      // <img src={`http://purecatamphetamine.github.io/country-flag-icons/3x2/${countrys}}CL.svg`} alt="GG"/>
      `http://purecatamphetamine.github.io/country-flag-icons/3x2/${countrys}}.svg`
    );
  };

    if (loading || error) {
    return <p>{error ? error.message : "Loading..."}</p>;
  }
  return (
    <>
      <Header></Header>
      {/* <h3>VersiÃ³n fruna {
        locale.length === 0 ? <h1>No carga</h1> : <h1>Cargo! {locale}</h1>}
        </h3> */}
      <Row style={{ marginTop: 20, marginBottom: 20 }}>
        <Col>
          <Label>Some random text {"\n"}</Label>
          {/* <div class="input-group input-group-sm mb-3">
  <span class="input-group-text" id="inputGroup-sizing-sm"></span><SearchIcon style={{fontSize: 30}} />
  <Input type="text" class="form-control" aria-label="Sizing example input" aria-describedby="inputGroup-sizing-"/>
</div> */}
          <div class="input-group input-group-lg">
          <SearchIcon style={{fontSize: 20}} />
          <input type="text" class="form-control" 
          placeholder="Recipient's username" 
          aria-describedby="button-addon2"
          style={{borderRadius: 5}}/>
          {"  "}
          <button class="btn btn-outline-secondary" type="button" id="button-addon2" style={{borderRadius: 5}}>Button</button>
        </div>
        </Col>
      </Row>
      <Row>

        {/* <Input style={{width: '100%'}}>
          <SearchIcon style={{fontSize: 30}} />
        </Input> */}
      </Row>
      <div className="containerInput">
        <input
          className="form-control inputBuscar"
          value={busqueda}
          placeholder="BÃºsqueda por Nombre o Empresa"
          onChange={handleChange}
        />
        <button className="btn btn-success">
        Click
        </button>
      </div>

      <div className="table-responsive">
       <br/>
       <br/>
      <table className="table table-sm table-bordered">
         <thead>
           <tr>
             <th>ID</th>
             <th>Nombre</th>
           </tr>
         </thead>

         <tbody>
           {data && 
           data.countries.map((country) => (
             <tr key={country.code}>
               <td>{country.code}</td>
               <td>{country.name}</td>
             </tr>
           ))}
         </tbody>

       </table>
       </div>


      <select
        value={country}
        onChange={(event) => {
          setCountry(event.target.value);
          // setValue(event.target.value)
          window.localStorage.setItem(
            "PAIS",
            JSON.stringify(event.target.value)
          );
          setLocal(event.target.value);
          console.log(country);
        }}
        onClick={() => defMonea(monea)}
      >
        {country &&
          data.countries.map((country) => (
            <option key={country.code} value={country.code}>
              {country.name} ||
              {/* <span>{" "}</span> */}
              Currency: {country.currency}
            </option>
          ))}
      </select>

      <div>
        {data.countries
          .filter((country) => country.name === { local })
          .map((countre) => (
            <h1>
              {/* <span>{" "}</span> */}
              Currencye: {countre.currency}
            </h1>
          ))}
      </div>
      <div className="label">{country}</div>
      <div className="value">
        {/* {Bandera(country)} */}
        <img
          // alt="United States"
          // src={pais}
          source={pais(local)}
          alt="gg"
        />
        {/* {pais(country)} */}
      </div>
      <h1>el kodigo de tu paÃ­ e: {country} ðŸ˜¸</h1>
      <h2>tu monea e: {local}</h2>
      <h4>LOCAL: {local}</h4>

      <Footer></Footer>
    </>
  );
};

ReactDOM.render(<CountrySelect />, document.getElementById("root"));
