import React, { useEffect, useState } from "react";
import ReactDOM from "react-dom";
import { ApolloClient, InMemoryCache, gql, useQuery } from "@apollo/client";
import Flags from "country-flag-icons/react/3x2";
import Header from "./components/header";
import Footer from "./components/footer";
import { Label, Row, Col, Input } from "reactstrap";
// initialize a GraphQL client
const client = new ApolloClient({
  cache: new InMemoryCache(),
  uri: "https://countries.trevorblades.com",
});

// write a GraphQL query that asks for names and codes for all countries
const LIST_COUNTRIES = gql`
  {
    countries {
      name
      code
      native
      capital
      emoji
      currency
      languages {
        code
        name
      }
    }
  }
`;

// create a component that renders a select input for coutries
const CountrySelect = () => {
  const [locale, setLocale] = useState("");
  const [local, setLocal] = useState(locale);

  const obtener = () => {
    setLocale(window.localStorage.getItem("PAIS"));
  };

  // const setValue = (value) => {
  //   try {
  //     window.localStorage.setItem("PAIS", JSON.stringify(value));
  //   } catch (err) {
  //     console.log(err);
  //   }
  // };

  useEffect(() => {
    obtener();
    const item = () => {
      setLocal(window.localStorage.getItem("PAIS"));
    };

    // setLocal(window.localStorage.getItem("PAIS"))
    item();
  }, [local, locale]);

  const [country, setCountry] = useState("");

  useEffect(() => {
    obtener();
    const item = () => {
      setCountry(window.localStorage.getItem("PAIS"));
    };

    // setLocal(window.localStorage.getItem("PAIS"))
    item();
  }, [country]);

  const [monea, setMonea] = useState("");
  // const [pais, setPais] = useState('');
  const { data, loading, error } = useQuery(LIST_COUNTRIES, { client });

  if (loading || error) {
    return <p>{error ? error.message : "Loading..."}</p>;
  }
  // const flagNationCode = ''
  // const Bandera = (flagNationCode) => {
  //   return <Flags.`${}` />;
  // };

  const defMonea = (item) => {
    return `"${item}"`;
  };
  const pais = (countrys) => {
    return (
      // <img src={`http://purecatamphetamine.github.io/country-flag-icons/3x2/${countrys}}CL.svg`} alt="GG"/>
      `http://purecatamphetamine.github.io/country-flag-icons/3x2/${countrys}}.svg`
    );
  };
  return (
    <div>
      <Header></Header>
      {/* <h3>VersiÃ³n fruna {
        locale.length === 0 ? <h1>No carga</h1> : <h1>Cargo! {locale}</h1>}
        </h3> */}
      <Row style={{ marginTop: 20, marginBottom: 20 }}>
        <Col>
          <Label>Some random text {"\n"}</Label>
        </Col>
      </Row>
      <Row>
        <Input>
        </Input>
      </Row>

      <select
        value={country}
        onChange={(event) => {
          setCountry(event.target.value);
          // setValue(event.target.value)
          window.localStorage.setItem(
            "PAIS",
            JSON.stringify(event.target.value)
          );
          setLocal(event.target.value);
          console.log(country);
        }}
        onClick={() => defMonea(monea)}
      >
        {country &&
          data.countries.map((country) => (
            <option key={country.code} value={country.code}>
              {country.name} ||
              {/* <span>{" "}</span> */}
              Currency: {country.currency}
            </option>
          ))}
      </select>

      <div>
        {data.countries
          .filter((country) => country.name === { local })
          .map((countre) => (
            <h1>
              {/* <span>{" "}</span> */}
              Currencye: {countre.currency}
            </h1>
          ))}
      </div>
      <div className="label">{country}</div>
      <div className="value">
        {/* {Bandera(country)} */}
        <img
          // alt="United States"
          // src={pais}
          source={pais(local)}
          alt="gg"
        />
        {/* {pais(country)} */}
      </div>
      <h1>el kodigo de tu paÃ­ e: {country} ðŸ˜¸</h1>
      <h2>tu monea e: {local}</h2>
      <h4>LOCAL: {local}</h4>

      <Footer></Footer>
    </div>
  );
};

ReactDOM.render(<CountrySelect />, document.getElementById("root"));
