// const COUNTRIES_QUERY = gql`
//   query Counties($code: String) {
//     countries(filter: { continent: { eq: $code } }) {
//       name
//       code
//       capital
//       continent {
//         name
//       }
//     }
//   }
// `;

const COUNTRIES_QUERY = gql`
  query Countries {
    countries {
      name
      code
      capital
      continent {
        name
        code
      }
    }
  }
`;


const CountryList = () => {

  const { data, loading, error } = useQuery(LIST_COUNTRIES, { client });
  if (loading) {
    return <p>Loading...</p>;
  }
  const { countries } = data;

  return (
    <>
      {countries.map((c, i) => (
        <div key={i}>
          {c.name} - {c.capital} - {c.continent.name}
        </div>
      ))}
    </>
  );
};


ReactDOM.render(<CountryList />, document.getElementById("root"));
