import React, { useEffect, useState } from "react";
import ReactDOM from "react-dom";
import { ApolloClient, InMemoryCache, gql, useQuery } from "@apollo/client";
import Flags from "country-flag-icons/react/3x2";
import Header from "./components/header";
import Footer from "./components/footer";
import { Label, Row, Col, Input, Button, Card, CardBody } from "reactstrap";
import SearchIcon from "@material-ui/icons/Search";
// initialize a GraphQL client

const client = new ApolloClient({
  cache: new InMemoryCache(),
  uri: "https://countries.trevorblades.com",
});

// write a GraphQL query that asks for names and codes for all countries
// const LIST_COUNTRIES = gql`
//   {
//     countries {
//       name
//       code
//       native
//       capital
//       emoji
//       currency
//       languages {
//         code
//         name
//       }
//     }
//   }
// `;

// interface Country {
//   name: string;
//   code: string;
//   capital: string;
//   continent: {
//     name: string;
//   };
// }

// interface CountryData {
//   countries: Country[];
// }

// const [CountryVariable] = useState({
//   code: ""
// })

// const COUNTRIES_QUERY = gql`
//   query Counties($code: String) {
//     countries(filter: { continent: { eq: $code } }) {
//       name
//       code
//       capital
//       continent {
//         name
//       }
//     }
//   }
// `;

// const COUNTRIES_QUERY = gql`
//   query Countries {
//     countries {
//       name
//       code
//       capital
//       continent {
//         name
//         code
//       }
//     }
//   }
// `;

// const COUNTRIES_QUERY = gql`
// query Counties($code: String) {
//   countries(filter: { continent: { eq: $code } }) {
//     name
//     code
//     capital
//     continent {
//       name
//       code
//     }
//   }
// }
// `;

const CountryList = () => {
  // const [conti, setConti] = useState("EU");
  const [continentCode, setContinentCode] = useState("");
  const [cont, setCont] = useState("");

  const COUNTRIES_QUERY = gql`
    query Counties {
      countries {
        name
        code
        capital
        continent {
          name
          code
        }
      }
    }
  `;

  const { data, loading, error } = useQuery(COUNTRIES_QUERY, {
    client,
    variables: {
      code: continentCode,
    },
  });

  function capital(string) {
    var gg = "";
    gg = console.log(
      string.charAt(0).toUpperCase() + string.slice(1).toLowerCase()
    );
    return gg;
  }

  const [approvalVariable, setApprovalVariable] = useState("");




  let filteredData = countries.filter((x) =>
    String(x.name).includes(approvalVariable)
  );

  if (loading) {
    return <p>Loading...</p>;
  }

  const { countries } = data;
  return (
    <>
      <Header></Header>

      <Row style={{ marginTop: 20, marginBottom: 20 }}>
        <Col>
          <h3>Some random text {"\n"}</h3>
          <div>
            <SearchIcon style={{ fontSize: 20 }} />
            <input
              type="text"
              style={{ borderRadius: 5 }}
              value={approvalVariable}
              onChange={(event) => {
                setApprovalVariable(event.target.value);
                console.log(event.target.value);
              }}
            />
            {"  "}
            <button
              class="btn btn-outline-secondary"
              type="button"
              id="button-addon2"
              style={{ borderRadius: 5 }}
              onClick={() => {
                let gg = capital(approvalVariable);
                setApprovalVariable(gg);
              }}
            >
              Button
            </button>
          </div>
        </Col>
      </Row>

      <Card>
        <Row style={{ flexDirection: "row", alignSelf: "auto" }}>
          <h2>Group by</h2>

          <Button variant="text">Continent</Button>
          <Button type="buttton" color="primary">
            Language
          </Button>
        </Row>
      </Card>

      {/* <span style={{margin: 20, marginTop: 100}}>{" "}</span> */}
      {/* <select
        style={{ margin: 40 }}
        value={cont}
        onChange={(event) => {
          setCont(event.target.value);
          console.log(event.target.value);
          setContinentCode(event.target.value)
        }}
      >
        <option value="AS">Asia</option>
        <option value="EU">EUROPA</option>
        <option value="SA">Sudamerica</option>
      </select> */}

      {/* <input
      style={{ margin: 40 }}
      value={cont}
      onChange={(event) => {
        setCont(event.target.value);
        console.log(event.target.value);
        setContinentCode(event.target.value)
      }}>

      </input> */}

      {/* {countries.length === 0 ? (
        <h1>GG</h1>
      ) : filteredData.map((c, i) => (
          <div key={c.code}>
            <h1>Asia</h1>
              {(c.continent.code !== "EU") ? <h1>GG</h1> : <h1>Europa</h1>
              }
            {(c.continent.code !== "EU") ? <h1>GG</h1> : c.name
              }
            <h1>Sudamerica</h1>
            {(c.continent.code !== 'SA') ? <h1>GG</h1>: c.name
              }
          </div>
        ))
      } */}
      {/* ==================== */}

      {countries.length === 0
        ? null
        : filteredData
            .filter((conti) => conti.continent.code === "AN")
            .slice(0, 1)
            .map((c, i) => (
              <div key={c.code}>
                <h1>Antarctica</h1>
              </div>
            ))}

      {countries.length === 0
        ? null
        : filteredData
            .filter((conti) => conti.continent.code.includes("AN"))
            .map((c, i) => <div key={c.code}>{c.name}</div>)}

      {/* ==================== */}

      {countries.length === 0
        ? null
        : filteredData
            .filter((conti) => conti.continent.code === "AS")
            .slice(0, 1)
            .map((c, i) => (
              <div key={c.code}>
                <h1>Asia</h1>
              </div>
            ))}

      {countries.length === 0
        ? null
        : filteredData
            .filter((conti) => conti.continent.code.includes("AS"))
            .map((c, i) => <div key={c.code}>{c.name}</div>)}

      {/* ==================== */}

      {countries.length === 0
        ? null
        : filteredData
            .filter((conti) => conti.continent.code === "EU")
            .slice(0, 1)
            .map((c, i) => (
              <div key={c.code}>
                <h1>Europe</h1>
              </div>
            ))}

      {countries.length === 0
        ? null
        : filteredData
            .filter((conti) => conti.continent.code.includes("EU"))
            .map((c, i) => <div key={c.code}>{c.name}</div>)}

      {/* ==================== */}

      {countries.length === 0
        ? null
        : filteredData
            .filter((conti) => conti.continent.code === "NA")
            .slice(0, 1)
            .map((c, i) => (
              <div key={c.code}>
                <h1>North America</h1>
              </div>
            ))}

      {countries.length === 0
        ? null
        : filteredData
            .filter((conti) => conti.continent.code.includes("NA"))
            .map((c, i) => <div key={c.code}>{c.name}</div>)}

      {/* ==================== */}

      {countries.length === 0
        ? null
        : filteredData
            .filter((conti) => conti.continent.code === "OC")
            .slice(0, 1)
            .map((c, i) => (
              <div key={c.code}>
                <h1>Oceania</h1>
              </div>
            ))}

      {countries.length === 0
        ? null
        : filteredData
            .filter((conti) => conti.continent.code.includes("OC"))
            .map((c, i) => <div key={c.code}>{c.name}</div>)}

      {/* ==================== */}

      {countries.length === 0
        ? null
        : filteredData
            .filter((conti) => conti.continent.code === "SA")
            .slice(0, 1)
            .map((c, i) => (
              <div key={c.code}>
                <h1>South America</h1>
              </div>
            ))}

      {countries.length === 0
        ? null
        : filteredData
            .filter((conti) => conti.continent.code.includes("SA"))
            .map((c, i) => <div key={c.code}>{c.name}</div>)}

      <Footer></Footer>
    </>
  );
};

ReactDOM.render(<CountryList />, document.getElementById("root"));
