import React, {useState} from 'react';
import ReactDOM from 'react-dom';
import {ApolloClient, InMemoryCache, gql, useQuery} from '@apollo/client';
import Flags from "country-flag-icons/react/3x2";

// initialize a GraphQL client
const client = new ApolloClient({
  cache: new InMemoryCache(),
  uri: 'https://countries.trevorblades.com'
});

// write a GraphQL query that asks for names and codes for all countries
const LIST_COUNTRIES = gql`
  {
    countries {
      name
      code
      native
      capital
      emoji
      currency
      languages {
        code
        name
      }
    }
  }
`;

// create a component that renders a select input for coutries
function CountrySelect() {
  const [country, setCountry] = useState('US');
  const {data, loading, error} = useQuery(LIST_COUNTRIES, {client});

  if (loading || error) {
    return <p>{error ? error.message : 'Loading...'}</p>;
  }
  // const flagNationCode = ''
  // const Bandera = (flagNationCode) => {
  //   return <Flags.`${}` />;
  // };
const pais = (pais) => {
  return `http://purecatamphetamine.github.io/country-flag-icons/3x2/${pais}CL.svg`
}
  return (
    <div>
    <select value={country} onChange={event => setCountry(event.target.value)}>
      {data.countries.map(country => (
        <option key={country.code} value={country.code}>
          {country.name}
        </option>
      ))}
    </select>
    <div className="label">{country.code}</div>
          <div className="value">
            {/* {Bandera(country)} */}
            <img
  alt="United States"
  src={pais(country)}
  />
          </div>
    <h1>{country}</h1>

    </div>
  )
}

ReactDOM.render(<CountrySelect />, document.getElementById('root'));
