    query Counties {
      countries {
        name
        code
        capital
        currency
        continent {
          name
          code
        }
        languages {
          code
          name
        }
      }
    }
  `;

  const { data, loading, error } = useQuery(COUNTRIES_QUERY, {
    client,
    variables: {
      code: continentCode,
    },
  });

  function capital(string) {
    var gg = "";
    gg = console.log(
